import { ThemeCustom } from '@/components/Theme/Theme';
import createStyles from '@material-ui/core/styles/createStyles';
import withStyles, { WithStyles } from '@material-ui/core/styles/withStyles';
import React, { ComponentType, Fragment } from 'react';
import { compose } from 'recompose';
import Grid from '@material-ui/core/Grid';
import GridContainer from '@/layouts/Grid/Container';
import Logo from '@/components/ToolBar/Logo';
interface IProps {
    classes?: any
}

const styles: any = (theme: ThemeCustom) => createStyles({
    logoWestay: {
        width: '100%',
        marginTop: '25px',
        marginBottom: '40px',
        textAlign: 'center',
    },
});

// @ts-ignore
const PrivacyPolicy: ComponentType<IProps> = (props: IProps) => {
    const { classes } = props;

    return (
        <Fragment>
            <GridContainer xs={9}>
                <Grid container>
                    <div className={classes.logoWestay}>
                        <Logo />
                    </div>
                    <div>
                        <h1>Chnh sch quyn ring t</h1>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>C&ocirc;ng ty C phn Westay (S ng k&yacute; c&ocirc;ng ty: 0108045140) (&ldquo;WeStay&rdquo; hoc &ldquo;ch&uacute;ng t&ocirc;i&rdquo;) s hu v&agrave; iu h&agrave;nh trang web Westay.vn. Ch&iacute;nh S&aacute;ch Quyn Ri&ecirc;ng T n&agrave;y m&ocirc; t c&aacute;ch thc ch&uacute;ng t&ocirc;i thu thp, s dng, tit l, x l&yacute; v&agrave; bo v th&ocirc;ng tin nhn dng c&aacute; nh&acirc;n (&ldquo;D Liu C&aacute; Nh&acirc;n&rdquo;) m&agrave; Qu&yacute; v (&ldquo;Ngi d&ugrave;ng&rdquo;) c&oacute; th cung cp li&ecirc;n quan n c&aacute;c dch v ca WeStay c cung cp th&ocirc;ng qua trang web: <a href='www.westay.vn'>www.westay.vn</a> (&ldquo;Trang Web&rdquo;). D Liu C&aacute; Nh&acirc;n c&oacute; ngha l&agrave; d liu, cho d&ugrave; l&agrave; &uacute;ng hoc kh&ocirc;ng &uacute;ng, v mt c&aacute; nh&acirc;n c&oacute; th c nhn dng t d liu &oacute;, hoc t d liu v&agrave; c&aacute;c th&ocirc;ng tin kh&aacute;c m&agrave; WeStay c&oacute; c hoc c&oacute; quyn truy cp. C&aacute;c  cp Trang Web c coi l&agrave; bao gm c c&aacute;c trang ph&aacute;i sinh, bao gm nhng kh&ocirc;ng gii hn  c&aacute;c trang web v&agrave; ng dng di ng ca Trang Web. Bng c&aacute;ch truy cp hoc s dng Trang Web, Qu&yacute; v ng &yacute; v&agrave; chp thun vic thu thp, s dng, tit l v&agrave; x l&yacute; D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v theo Ch&iacute;nh S&aacute;ch Quyn Ri&ecirc;ng T n&agrave;y v&agrave;/hoc iu Khon Trang Web. Nu Qu&yacute; v kh&ocirc;ng ng &yacute; vi Ch&iacute;nh S&aacute;ch Quyn Ri&ecirc;ng T di &acirc;y, xin vui l&ograve;ng ngay lp tc ri khi Trang Web n&agrave;y.</span></p>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}><strong>&nbsp;</strong></span></p>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>T&ugrave;y tng thi im, WeStay c&oacute; th sa i Ch&iacute;nh S&aacute;ch Quyn Ri&ecirc;ng T  tng th&iacute;ch vi nhng thay i v ph&aacute;p lut, c&aacute;c thc tin thu thp v&agrave; s dng D Liu C&aacute; Nh&acirc;n ca ch&uacute;ng t&ocirc;i, c&aacute;c t&iacute;nh nng ca Trang Web, hoc nhng tin b v c&ocirc;ng ngh. Nu ch&uacute;ng t&ocirc;i thc hin c&aacute;c sa i m&agrave; l&agrave;m thay i c&aacute;ch thc ch&uacute;ng t&ocirc;i thu thp hoc s dng D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v, th&igrave; nhng sa i &oacute; s c ng trong Ch&iacute;nh S&aacute;ch Quyn Ri&ecirc;ng T n&agrave;y v&agrave; ng&agrave;y c&oacute; hiu lc s c n&ecirc;u  ngay phn u ca Ch&iacute;nh S&aacute;ch Quyn Ri&ecirc;ng T n&agrave;y. V&igrave; vy, Qu&yacute; v n&ecirc;n xem mt c&aacute;ch nh k Ch&iacute;nh S&aacute;ch Quyn Ri&ecirc;ng T n&agrave;y  c cp nht v c&aacute;c ch&iacute;nh s&aacute;ch v&agrave; thc tin hin h&agrave;nh ca ch&uacute;ng t&ocirc;i. WeStay cng s ng ni bt nhng thay i quan trng nh vy trc khi &aacute;p dng c&aacute;c thay i. Nu Qu&yacute; v kh&ocirc;ng ng &yacute; vi bt k thay i hoc sa i n&agrave;o trong Ch&iacute;nh S&aacute;ch Quyn Ri&ecirc;ng T, xin vui l&ograve;ng kh&ocirc;ng tip tc s dng Trang Web. Qu&yacute; v s c xem nh &atilde; ng &yacute; vi bt k s sa i ca Ch&iacute;nh S&aacute;ch Quyn Ri&ecirc;ng T khi Qu&yacute; v s dng Trang Web sau ng&agrave;y c&oacute; hiu lc ca sa i &oacute;.</span></p>
                        <h2 dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>1. Th&ocirc;ng tin ch&uacute;ng t&ocirc;i thu thp</span></h2>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Ch&uacute;ng t&ocirc;i thu thp D Liu C&aacute; Nh&acirc;n v Qu&yacute; v m&agrave; Qu&yacute; v cung cp cho ch&uacute;ng t&ocirc;i khi s dng Trang Web. D Liu C&aacute; Nh&acirc;n n&agrave;y bao gm nhng kh&ocirc;ng gii hn  t&ecirc;n, th&ocirc;ng tin nhn dng Ngi D&ugrave;ng WeStay v&agrave; th&ocirc;ng tin ng nhp, a ch, s in thoi, a ch email, t&ecirc;n kh&aacute;ch sn, v tr&iacute; kh&aacute;ch sn v&agrave;/hoc thi gian lu tr&uacute; ca Qu&yacute; v. Ch&uacute;ng t&ocirc;i c&oacute; th y&ecirc;u cu Qu&yacute; v cung cp th&ocirc;ng tin v s th&iacute;ch du lch ca Qu&yacute; v, h&atilde;ng h&agrave;ng kh&ocirc;ng s dng thng xuy&ecirc;n, v&agrave; phn hi v nhng tri nghim du lch ca Qu&yacute; v th&ocirc;ng qua vic t ch ca Qu&yacute; v tr&ecirc;n WeStay. Ch&uacute;ng t&ocirc;i cng thu thp th&ocirc;ng tin kh&ocirc;ng nhn dng c&aacute; nh&acirc;n (th&ocirc;ng tin kh&ocirc;ng th s dng  nhn dng Qu&yacute; v), bao gm nhng kh&ocirc;ng gii hn  a ch IP, d liu v v tr&iacute; a l&yacute;, loi h iu h&agrave;nh, quc tch, u ti&ecirc;n t&igrave;m kim, cng nh d liu li&ecirc;n quan n vic s dng Internet n&oacute;i chung.</span></p>
                        <h2 dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>2. Ch&uacute;ng t&ocirc;i s dng Th&ocirc;ng tin ca Qu&yacute; v nh th n&agrave;o</span></h2>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Ch&uacute;ng t&ocirc;i c&oacute; th s dng D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v v&agrave; c&aacute;c th&ocirc;ng tin kh&aacute;c thu thp qua Trang Web, cho c&aacute;c mc &iacute;ch sau: ng k&yacute;, qun l&yacute; v&agrave;/hoc qun tr vic Qu&yacute; v s dng v&agrave;/hoc truy cp Trang Web; qun l&yacute;, iu h&agrave;nh, qun tr v&agrave; cung cp cho Qu&yacute; v c&aacute;c dch v c  ngh tr&ecirc;n Trang Web;  li&ecirc;n h vi Qu&yacute; v v nhng vn  li&ecirc;n quan n vic Qu&yacute; v s dng v&agrave;/hoc truy cp Trang Web v&agrave; c&aacute;c dch v tr&ecirc;n Trang Web, v&agrave; bt c c&acirc;u hi v&agrave;/hoc thc mc n&agrave;o m&agrave; Qu&yacute; v a ra th&ocirc;ng qua Trang Web hay bng c&aacute;ch kh&aacute;c; t&ugrave;y chnh tri nghim ca Qu&yacute; v khi s dng Trang Web; &aacute;nh gi&aacute; v&agrave; ci thin s tri nghim v&agrave; s h&agrave;i l&ograve;ng ca kh&aacute;ch h&agrave;ng; c&ocirc;ng b &aacute;nh gi&aacute; ca kh&aacute;ch h&agrave;ng, chng hn nh v Trang Web, c&aacute;c i l&yacute; Du Lch, c&aacute;c Chuyn Bay hoc Kh&aacute;ch Sn,  nh dng k thut s v&agrave;/hoc dng bn in cho vic truy cp c&ocirc;ng cng;  thc thi c&aacute;c iu Khon v&agrave; iu Kin ca ch&uacute;ng t&ocirc;i;  gii quyt c&aacute;c tranh chp hoc khiu ni, thu tin hay c&aacute;c khon ph&iacute;, hoc khc phc s c; v&agrave;/hoc cho c&aacute;c mc &iacute;ch kh&aacute;c c th&ocirc;ng b&aacute;o cho Qu&yacute; v ti thi im thu thp.</span></p>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}><strong>&nbsp;</strong></span></p>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Ch&uacute;ng t&ocirc;i cng c&oacute; th s dng D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v v&agrave; c&aacute;c th&ocirc;ng tin kh&aacute;c c thu thp cho mc &iacute;ch tip th qua Mng Truyn Th&ocirc;ng Mng X&atilde; Hi bng c&aacute;ch s dng k thut  th trc tip v&agrave; m rng (direct and open graph technology) v&agrave; cho c&aacute;c mc &iacute;ch tip th k thut s v&agrave; phng thc truyn thng nh gi bu phm trc tip n Qu&yacute; v v&agrave; ng b&agrave;i v c&aacute;c sn phm mi, c&aacute;c ch&agrave;o b&aacute;n c bit hoc c&aacute;c th&ocirc;ng tin kh&aacute;c m&agrave; ch&uacute;ng t&ocirc;i cho rng Qu&yacute; v c&oacute; th quan t&acirc;m. Xin lu &yacute; rng Qu&yacute; v c&oacute; th t chi bt k t&agrave;i liu tip th n&agrave;o m&agrave; ch&uacute;ng t&ocirc;i gi cho Qu&yacute; v v&agrave;o bt c l&uacute;c n&agrave;o. H&atilde;y l&agrave;m theo c&aacute;c hng dn hy b ng k&yacute; c n&ecirc;u trong t&agrave;i liu tip th ca ch&uacute;ng t&ocirc;i nu Qu&yacute; v t chi bt c t&agrave;i liu tip th n&agrave;o v&agrave; ch&uacute;ng t&ocirc;i s t&ocirc;n trng mong mun ca Qu&yacute; v.</span></p>
                        <h2 dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>3. Chia s v&agrave; chuyn D liu C&aacute; nh&acirc;n ca Qu&yacute; v</span></h2>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v s/c&oacute; th c WeStay tit l cho c&aacute;c c&ocirc;ng ty li&ecirc;n kt ca WeStay. T&ugrave;y tng thi im, WeStay (v&agrave;/hoc c&aacute;c c&ocirc;ng ty li&ecirc;n kt ca ch&uacute;ng t&ocirc;i) cng c&oacute; th tit l D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v vi nhng nh&agrave; cung cp, cung ng hay i l&yacute; dch v b&ecirc;n th ba, cho mt hoc nhiu hn c&aacute;c mc &iacute;ch n&ecirc;u tr&ecirc;n. Dch v ca b&ecirc;n th ba bao gm nhng kh&ocirc;ng gii hn  dch v m&aacute;y ch website, ph&acirc;n t&iacute;ch d liu, tip th, x l&yacute; c&aacute;c giao dch th t&iacute;n dng, v&agrave; cung cp dch v.</span></p>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}><strong>&nbsp;</strong></span></p>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Trong mt s trng hp, khi Qu&yacute; v giao dch trc tip vi mt b&ecirc;n th ba  tip nhn dch v, hoc khi Qu&yacute; v s dng Trang Web  t ph&ograve;ng kh&aacute;ch sn hoc tip nhn sn phm hay dch v kh&aacute;c, ch&uacute;ng t&ocirc;i c&oacute; th cung cp D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v cho homestay &oacute; hoc b&ecirc;n th ba kh&aacute;c &oacute;.</span></p>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}><strong>&nbsp;</strong></span></p>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>T&ugrave;y thuc v&agrave;o v tr&iacute; ca Qu&yacute; v, WeStay, c&aacute;c c&ocirc;ng ty li&ecirc;n kt v&agrave; c&aacute;c nh&agrave; cung cp, nh&agrave; cung ng hoc i l&yacute; dch v b&ecirc;n th ba ca WeStay, cng c&oacute; th chuyn D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v ra nc ngo&agrave;i.</span></p>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}><strong>&nbsp;</strong></span></p>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Xin lu &yacute; rng WeStay cng c&oacute; th tit l D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v trong c&aacute;c trng hp sau &acirc;y:  a ra v&agrave; phn bin chng li bt k khiu ni hoc khiu kin n&agrave;o;  thc hin lnh ca t&ograve;a &aacute;n, quy tr&igrave;nh ph&aacute;p l&yacute;, c&aacute;c y&ecirc;u cu hp ph&aacute;p, c&aacute;c lnh hoc y&ecirc;u cu tng ng ca c&aacute;c t chc thc thi ph&aacute;p lut hoc c&aacute;c c quan c&oacute; thm quyn;  iu tra gian ln hoc sai tr&aacute;i kh&aacute;c theo y&ecirc;u cu hoc cn thit  tu&acirc;n th bt k lut &aacute;p dng, hoc  bo v li &iacute;ch hp ph&aacute;p ca ch&uacute;ng t&ocirc;i; cho ngi mua li&ecirc;n quan n bt k vic mua b&aacute;n, chuyn nhng, hoc chuyn giao to&agrave;n b hoc mt phn c&ocirc;ng vic kinh doanh hoc c&ocirc;ng ty ch&uacute;ng t&ocirc;i;  thc thi hoc &aacute;p dng c&aacute;c iu khon v&agrave; iu kin &aacute;p dng i vi c&aacute;c sn phm v&agrave; dch v ca ch&uacute;ng t&ocirc;i;  bo v c&aacute;c quyn, t&agrave;i sn hoc s an to&agrave;n ca Traveloka, Ngi D&ugrave;ng Trang Web, hoc bt k ngi n&agrave;o kh&aacute;c theo quyt nh ca Traveloka; v&agrave; trong bt k t&igrave;nh hung n&agrave;o kh&aacute;c c ph&aacute;p lut cho ph&eacute;p.</span></p>
                        <h2 dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>4. Chp thun</span></h2>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Nh &atilde; n&ecirc;u  tr&ecirc;n, bng c&aacute;ch truy cp v&agrave; s dng Trang Web, hoc bng c&aacute;ch t h&agrave;ng t Trang Web, hoc bng c&aacute;ch ng k&yacute; hay s dng c&aacute;c dch v tr&ecirc;n Trang Web, hoc bng c&aacute;ch nhn v&agrave;o n&uacute;t &ldquo;X&aacute;c Nhn&rdquo; hoc c&aacute;ch tng ng khi to mt t&agrave;i khon mi tr&ecirc;n Trang Web, Qu&yacute; v: chp thun cho WeStay v&agrave;/hoc c&aacute;c c&ocirc;ng ty li&ecirc;n kt ca WeStay thu thp, s dng, tit l v&agrave;/hoc x l&yacute; D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v c  cp  tr&ecirc;n cho c&aacute;c mc &iacute;ch nh &atilde; m&ocirc; t  tr&ecirc;n; v&agrave; chp thun cho WeStay v&agrave;/hoc c&aacute;c c&ocirc;ng ty li&ecirc;n kt ca WeStay chuyn giao D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v cho c&aacute;c c&ocirc;ng ty li&ecirc;n kt ca WeStay kh&aacute;c n ni m&agrave; c&aacute;c c&ocirc;ng ty &oacute; ta lc, v&agrave; n c&aacute;c nh&agrave; cung cp, nh&agrave; cung ng, hoc i l&yacute; dch v b&ecirc;n th ba ca WeStay (hoc c&aacute;c c&ocirc;ng ty li&ecirc;n kt ca WeStay) cho c&aacute;c mc &iacute;ch nh &atilde; m&ocirc; t  tr&ecirc;n.</span></p>
                        <h2 dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>5. R&uacute;t li chp thun</span></h2>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Qu&yacute; v c&oacute; th r&uacute;t li s chp thun ca Qu&yacute; v i vi vic cho ch&uacute;ng t&ocirc;i thu thp, s dng hoc tit l D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v bt c l&uacute;c n&agrave;o, bng c&aacute;ch gi cho ch&uacute;ng t&ocirc;i th&ocirc;ng b&aacute;o hp l&yacute;. Nu Qu&yacute; v mun r&uacute;t li s chp thun ca Qu&yacute; v, xin vui l&ograve;ng th&ocirc;ng b&aacute;o cho ch&uacute;ng t&ocirc;i theo a ch li&ecirc;n h c lit k&ecirc; di &acirc;y. Ch&uacute;ng t&ocirc;i s ngng thu thp, s dng hoc tit l D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v khi c th&ocirc;ng b&aacute;o, tr khi lut ph&aacute;p y&ecirc;u cu hoc nu ch&uacute;ng t&ocirc;i c&oacute; hot ng kinh doanh hp ph&aacute;p hoc cho c&aacute;c mc &iacute;ch hp ph&aacute;p  duy tr&igrave; D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v. Lu &yacute; rng bng c&aacute;ch r&uacute;t s chp thun ca Qu&yacute; v i vi vic thu thp, s dng hoc tit l D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v, ch&uacute;ng t&ocirc;i kh&ocirc;ng th tip tc cung cp cho Qu&yacute; v c&aacute;c dch v ca ch&uacute;ng t&ocirc;i v&agrave; Qu&yacute; v ng &yacute; rng ch&uacute;ng t&ocirc;i s kh&ocirc;ng chu tr&aacute;ch nhim vi Qu&yacute; v v bt k tn tht hoc thit hi n&agrave;o ph&aacute;t sinh t hoc c&oacute; li&ecirc;n quan n vic chm dt c&aacute;c dch v n&agrave;y.</span></p>
                        <h2 dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>6. Ch&iacute;nh s&aacute;ch Cookie</span></h2>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Mt &ldquo;cookie&rdquo; l&agrave; mt tp nhn din ch v&agrave; s m&agrave; ch&uacute;ng t&ocirc;i chuyn n  a cng hoc th nh ca Qu&yacute; v th&ocirc;ng qua tr&igrave;nh duyt web khi Qu&yacute; v truy cp Trang Web ca ch&uacute;ng t&ocirc;i. N&oacute; cho ph&eacute;p h thng ca ch&uacute;ng t&ocirc;i nhn ra Qu&yacute; v khi Qu&yacute; v truy cp tr li Trang Web ca ch&uacute;ng t&ocirc;i v&agrave; ci thin dch v ca ch&uacute;ng t&ocirc;i cung cp cho Qu&yacute; v. C&aacute;c th&ocirc;ng tin c s dng  theo d&otilde;i vic s dng Trang Web bi ngi truy cp v&agrave; thu thp c&aacute;c b&aacute;o c&aacute;o thng k&ecirc; v hot ng Trang Web.  bit th&ecirc;m th&ocirc;ng tin v cookie xin vui l&ograve;ng truy cp: <a href='www.aboutcookies.org'>www.aboutcookies.org</a> hoc <a href='www.allaboutcookies.org'>www.allaboutcookies.org</a>. Cookie cng c&oacute; th c s dng  thu thp th&ocirc;ng tin tng hp v c&aacute;c khu vc m&agrave; Trang Web ca ch&uacute;ng t&ocirc;i c truy cp thng xuy&ecirc;n nht. Th&ocirc;ng tin v lu lng n&agrave;y c&oacute; th c s dng  ci thin ni dung Trang Web ca ch&uacute;ng t&ocirc;i v&agrave; l&agrave;m cho vic s dng ca Qu&yacute; v d d&agrave;ng hn. Nu Qu&yacute; v mun t chi cookie ca ch&uacute;ng t&ocirc;i, Qu&yacute; v c&oacute; th t&ugrave;y chnh cu h&igrave;nh tr&igrave;nh duyt ca Qu&yacute; v. Tuy nhi&ecirc;n, mt s t&iacute;nh nng Trang Web ca ch&uacute;ng t&ocirc;i c&oacute; th kh&ocirc;ng hot ng nu Qu&yacute; v loi b c&aacute;c tp cookie khi tr&igrave;nh duyt ca Qu&yacute; v. Nu Qu&yacute; v kh&ocirc;ng cho ph&eacute;p cookie, Qu&yacute; v c&oacute; th kh&ocirc;ng c&oacute; kh nng truy cp v&agrave;o c&aacute;c chc nng hoc t&iacute;nh nng quan trng tr&ecirc;n Trang Web n&agrave;y v&agrave; Qu&yacute; v ch c&oacute; th s dng Trang Web mt c&aacute;ch hn ch.</span></p>
                        <h2 dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>7. Truy cp hoc chnh sa D liu C&aacute; nh&acirc;n ca Qu&yacute; v</span></h2>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Khi Qu&yacute; v cung cp cho ch&uacute;ng t&ocirc;i D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v, h&atilde;y m bo rng D Liu C&aacute; Nh&acirc;n &oacute; l&agrave; ch&iacute;nh x&aacute;c v&agrave; y . Nu Qu&yacute; v tin rng bt k th&ocirc;ng tin n&agrave;o ca Qu&yacute; v do ch&uacute;ng t&ocirc;i s hu hoc kim so&aacute;t c&oacute; sai s&oacute;t hoc thiu s&oacute;t, xin vui l&ograve;ng ng nhp v&agrave;o t&agrave;i khon ca Qu&yacute; v tr&ecirc;n Trang Web v&agrave; chnh sa th&ocirc;ng tin. Ngo&agrave;i ra, vui l&ograve;ng cp nht D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v th&ocirc;ng qua t&agrave;i khon ca Qu&yacute; v mt c&aacute;ch kp thi khi c&oacute; bt k thay i n&agrave;o.</span></p>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}><strong>&nbsp;</strong></span></p>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Nu Qu&yacute; v mun chnh sa li hoc thiu s&oacute;t trong bt k D Liu C&aacute; Nh&acirc;n n&agrave;o thuc s hu hoc kim so&aacute;t ca ch&uacute;ng t&ocirc;i m&agrave; kh&ocirc;ng th thc hin th&ocirc;ng qua Trang Web ca ch&uacute;ng t&ocirc;i, hoc truy cp v&agrave;o D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v thuc s hu hoc kim so&aacute;t ca ch&uacute;ng t&ocirc;i, hoc nh c quy nh theo ph&aacute;p lut hin h&agrave;nh, xin vui l&ograve;ng gi y&ecirc;u cu ca Qu&yacute; v n a ch li&ecirc;n lc chi tit ca ch&uacute;ng t&ocirc;i c lit k&ecirc; b&ecirc;n di. Ch&uacute;ng t&ocirc;i c&oacute; th thu mt khon ph&iacute; hp l&yacute; cho vic truy cp v&agrave;o D Liu C&aacute; Nh&acirc;n, cho mc &iacute;ch thu hi c&aacute;c chi ph&iacute; ph&aacute;t sinh tng ng vi y&ecirc;u cu truy cp.</span></p>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}><strong>&nbsp;</strong></span></p>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}> bo v s ri&ecirc;ng t v&agrave; an to&agrave;n ca Qu&yacute; v, ch&uacute;ng t&ocirc;i s x&aacute;c minh danh t&iacute;nh ca Qu&yacute; v trc khi cp quyn truy cp hoc thc hin vic sa i D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v. Ch&uacute;ng t&ocirc;i s c gng &aacute;p ng c&aacute;c y&ecirc;u cu truy cp hoc chnh sa D Liu C&aacute; Nh&acirc;n ca Qu&yacute; v sm nht c&oacute; th, tr khi c&oacute; ngoi l &aacute;p dng.</span></p>
                        <h2 dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>8. Bo v D liu C&aacute; nh&acirc;n ca Qu&yacute; v</span></h2>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Ch&uacute;ng t&ocirc;i bo v D Liu C&aacute; Nh&acirc;n thuc s hu hoc kim so&aacute;t ca ch&uacute;ng t&ocirc;i bng c&aacute;ch duy tr&igrave; c&aacute;c bin ph&aacute;p an ninh hp l&yacute;, bao gm c c&aacute;c quy tr&igrave;nh v vt cht, k thut v&agrave; t chc,  ngn chn vic truy cp, thu thp, s dng, tit l, sao ch&eacute;p, sa i, hy b mt c&aacute;ch tr&aacute;i ph&eacute;p hoc nhng ri ro tng t.</span></p>
                        <h2 dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>9. Li&ecirc;n kt n c&aacute;c trang Web kh&aacute;c</span></h2>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Trang Web ca ch&uacute;ng t&ocirc;i c&oacute; th cha c&aacute;c li&ecirc;n kt n c&aacute;c trang web c quan t&acirc;m kh&aacute;c. Tuy nhi&ecirc;n, khi Qu&yacute; v &atilde; s dng c&aacute;c li&ecirc;n kt n&agrave;y  ri khi Trang Web ca ch&uacute;ng t&ocirc;i, Qu&yacute; v n&ecirc;n lu &yacute; rng ch&uacute;ng t&ocirc;i kh&ocirc;ng c&oacute; bt k quyn kim so&aacute;t n&agrave;o vi c&aacute;c trang web kh&aacute;c &oacute;. Xin lu &yacute; rng ch&uacute;ng t&ocirc;i kh&ocirc;ng chu tr&aacute;ch nhim v vic thc hin quyn ri&ecirc;ng t ca nhng trang web &oacute; v&agrave; khuyn c&aacute;o Qu&yacute; v n&ecirc;n c c&aacute;c quy nh v quyn ri&ecirc;ng t ca mi trang web Qu&yacute; v truy cp, m&agrave; trang web n&agrave;y c&oacute; thu thp th&ocirc;ng tin c&aacute; nh&acirc;n ca Qu&yacute; v.</span></p>
                        <h2 dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>10. ng k&yacute; v&agrave; Th&agrave;nh vi&ecirc;n</span></h2>
                        <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Trang Web n&agrave;y cho ph&eacute;p Qu&yacute; v to mt t&agrave;i khon Ngi D&ugrave;ng bng vic ng k&yacute; th&agrave;nh vi&ecirc;n, da tr&ecirc;n c&aacute;c d liu Qu&yacute; v cung cp. Bng c&aacute;ch cung cp c&aacute;c d liu, ng k&yacute; v&agrave; to t&agrave;i khon, Qu&yacute; v phi m bo rng:</span></p>
                        <ul>
                            <li dir='ltr' style={{ listStyleType: 'disc' }}>
                                <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Qu&yacute; v &atilde; bng hoc tr&ecirc;n 18 tui.</span></p>
                            </li>
                            <li dir='ltr' style={{ listStyleType: 'disc' }}>
                                <p dir='ltr'><span style={{ fontFamily: 'helvetica, arial, sans-serif' }}>Th&ocirc;ng tin v Qu&yacute; v l&agrave; &uacute;ng v&agrave; ch&iacute;nh x&aacute;c, hin thi v&agrave; y  theo y&ecirc;u cu trong mu ng k&yacute; tr&ecirc;n Trang Web (&ldquo;D Liu ng K&yacute;&rdquo;).</span></p>
                            </li>
