import { ThemeCustom } from "@/components/Theme/Theme";
import fakeIMG from "@/assets/room_demo.jpeg";
import align from "@/styles/Position/align.module.scss";
import mapMarker from "@/assets/SvgIcon/map-marker.svg";
import discount from "@/assets/SvgIcon/discount.svg";
import createStyles from "@material-ui/core/styles/createStyles";
import withStyles from "@material-ui/core/styles/withStyles";
import React, {
  ComponentType,
  Fragment,
  useState,
  useContext,
  memo,
  useEffect
} from "react";
import { compose } from "recompose";
import Grid from "@material-ui/core/Grid/Grid";
import Slider, { Settings } from "react-slick";
import Typography from "@material-ui/core/Typography/Typography";
import StarRatings from "react-star-ratings";
import classNames from "classnames";
import _ from "lodash";
import SvgCustom from "@/components/Custom/SvgCustom";
import Paper from "@material-ui/core/Paper/Paper";
import { formatMoney } from "@/utils/mixins";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "@/styles/PageProfile/StylePageProfile.scss";
import "@/styles/CustomizeSlick.scss";
import Tooltip from "@material-ui/core/Tooltip/Tooltip";
import Button from "@material-ui/core/Button/Button";
import { RoomIndexRes } from "@/types/Requests/Rooms/RoomResponses";
import { ThemeStyle } from "@material-ui/core/styles/createTypography";
import Hidden from "@material-ui/core/Hidden/Hidden";
import { GlobalContext, IGlobalContext } from "@/store/context/GlobalContext";
import LazyLoad from "react-lazyload";
import FavoriteAnimation from "@/components/IntroHomestay/FavoriteAnimation";

import { windowExist } from "@/index";

import StarIcon from "@material-ui/icons/StarRounded";
import QuickBookIcon from "@material-ui/icons/OfflineBoltRounded";
import Link from "@material-ui/core/Link";
import PrevArrowSlider from "@/views/DetailsPage/PrevArrowSlider";
import NextArrowSlider from "@/views/DetailsPage/NextArrowSlider";

const styles: any = (theme: ThemeCustom) =>
  createStyles({
    paper: {
      position: "relative",
      color: "#484848",
      height: 200,
      border: "1px solid #ebebeb",
      borderRadius: 4,
      "&:hover": {
        boxShadow: "rgba(0,0,0,0.12) 0px 0px 12px"
      },
      [theme!.breakpoints!.only!("xs")]: {
        height: "100%"
      }
    },
    boxImg: {
      position: "relative",
      height: "100%",
      padding: 4,
      [theme!.breakpoints!.only!("xs")]: {
        maxWidth: "calc(93vw - 4px)",
        height: "25vh"
      }
    },
    imgSize: {
      position: "relative",
      objectFit: "cover",
      height: "100%",
      verticalAlign: "middle",
      borderRadius: 4
    },
    wrapper: {
      height: "100%"
    },
    boxCard: {
      width: "100%",
      verticalAlign: "middle"
    },
    cardContainer: {
      height: "100%",
      padding: "12px 16px",
      boxSizing: "border-box"
    },
    cardWrapper: {
      position: "relative",
      height: "100%",
      width: "100%",
      display: "inline-block"
    },
    boxLink: {
      display: "block",
      color: "#484848",
      textDecoration: "none !important",
      cursor: "pointer",
      fontWeight: 600
    },
    boxTitle: {
      marginBottom: 4,
      paddingRight: "40px !important"
    },
    roomName: {
      overflow: "hidden",
      fontSize: 20,
      fontWeight: 700,
      // maxHeight: 44,
      overflowWrap: "break-word",
      textOverflow: "ellipsis",
      color: "#484848",
      lineHeight: "24px",
      display: "flex",
      [theme!.breakpoints!.only!("xs")]: {
        marginBottom: 10
      }
    },

    collectionAmenities: {
      overflowWrap: "break-word",
      fontSize: 14,

      color: "#484848"
    },

    dotAmenties: {
      display: "inline-block",
      verticalAlign: "super",
      margin: "0 3px"
    },
    roomType: {
      textTransform: "uppercase",
      fontSize: 12,
      fontWeight: 500,
      color: "#ff9800",
      fontFamily:
        "Circular, -apple-system, BlinkMacSystemFont, Roboto, 'Helvetica Neue', sans-serif",
      display: "inline-block",
      marginRight: 3
    },
    address: {
      fontSize: 14,
      [theme!.breakpoints!.only!("xs")]: {
