// @ts-ignore
const RegisterForm: ComponentType<IProps> = (props: RegisterLocalProps) => {
  const {
    classes,
    animation,
    handleSignUpAnimation,
    handleLoginButton
  } = props;

  const [showPassWord, setShowPassWord] = useState<boolean>(false);
  const [openSnack, setOpenSnack] = useState<boolean>(false);

  const { width } = useContext<IGlobalContext>(GlobalContext);

  const formikInit: FormikRegisterValues = useMemo(() => {
    return {
      name: "",
      phone: "",
      birthday: "",
      email: "",
      password: "",
      passwordConfirm: "",
      agreeTerms: false
    };
  }, []);

  const handleModalClose = () => {
    handleSignUpAnimation(false);
  };

  return (
    <Fragment>
      <Modal
        open={animation.isSignUpFormOpen}
        onClose={handleModalClose}
        disableAutoFocus
      >
        <Zoom in={animation.isSignUpFormOpen}>
          <Paper className={classes.modal} elevation={10}>
            <Grid container>
              <Grid item xs={4} className={classes.backgroundLogin}>
                <Typography variant="h5" className={classes.title}>
                  Đăng ký tài khoản
                </Typography>
              </Grid>
              <Grid item xs={8} className={classes.contentRight}>
                <Formik
                  initialValues={formikInit}
                  validationSchema={FormValidationSchema}
                  validateOnChange={false}
                  onSubmit={(
                    values: FormikRegisterValues,
                    action: FormikActions<FormikRegisterValues>
                  ) => {
                    const request: RegisterReq = {
                      email: values.email,
                      name: values.name,
                      phone: values.phone,
                      password: values.password,
                      password_confirmation: values.passwordConfirm,
                      type: 0
                    };

                    axios
                      .post("register", request)
                      .then((res: AxiosResponse<LoginRes>) => {
                        const data = res.data;
                        const cookies = new Cookies();

                        setOpenSnack(true);
                        cookies.set("_token", data.access_token, {
                          path: "/",
                          maxAge: data.expires_in
                        });
                        location.reload();

                        action.setSubmitting(false);
                      })
                      .catch((err: AxiosErrorCustom<ErrorValidate>) => {
                        const errors = err.response!.data.data.errors;

                        if (errors.email)
                          action.setFieldError("email", errors.email[0]);
                        action.setSubmitting(false);
                      });
                  }}
                >
                  {({
                    values,
                    errors,
                    touched,
                    handleChange,
                    handleBlur,
                    handleSubmit,
                    isSubmitting
                  }: FormikProps<FormikRegisterValues>) => {
                    return (
                      <form onSubmit={handleSubmit}>
                        {/*<FormControl fullWidth className = {classes.spaceTop}*/}
                        {/*error = {!!(errors.name && touched.name)}>*/}
                        {/*<InputLabel htmlFor = 'name'>Tên</InputLabel>*/}
                        {/*<Input*/}
                        {/*id = 'name'*/}
                        {/*name = 'name'*/}
                        {/*value = {values.name}*/}
                        {/*onChange = {handleChange}*/}
                        {/*onBlur = {handleBlur}*/}
                        {/*endAdornment = {<UserInputAdornment />}*/}
                        {/*/>*/}
                        {/*<FormHelperText>{touched.name ? errors.name : ''}</FormHelperText>*/}
                        {/*</FormControl>*/}
                        {/*<FormControl fullWidth className = {classes.spaceTop}*/}
                        {/*error = {!!(errors.phone && touched.phone)}>*/}
                        {/*<InputLabel htmlFor = 'phone'>Số điện thoại</InputLabel>*/}
                        {/*<Input*/}
                        {/*id = 'phone'*/}
                        {/*name = 'phone'*/}
                        {/*value = {values.phone}*/}
                        {/*onChange = {handleChange}*/}
                        {/*onBlur = {handleBlur}*/}
                        {/*endAdornment = {<PhoneInputAdornment />}*/}
                        {/*/>*/}
                        {/*<FormHelperText>{touched.phone ? errors.phone : ''}</FormHelperText>*/}
                        {/*</FormControl>*/}
                        <FormControl
                          fullWidth
                          className={classes.spaceTop}
                          error={!!(errors.email && touched.email)}
                        >
                          <InputLabel htmlFor="email">Email</InputLabel>
                          <Input
                            id="email"
                            name="email"
                            value={values.email}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            endAdornment={<EmailInputAdornment />}
                          />
                          <FormHelperText>
                            {touched.email ? errors.email : ""}
                          </FormHelperText>
                        </FormControl>
                        <FormControl
                          fullWidth
                          className={classes.spaceTop}
                          error={!!(errors.password && touched.password)}
                        >
                          <InputLabel htmlFor="password">Mật khẩu</InputLabel>
                          <Input
                            type={showPassWord ? "text" : "password"}
                            name="password"
                            id="password"
                            value={values.password}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            endAdornment={
                              <PasswordInputAdornment
                                isShown={showPassWord}
                                handle={setShowPassWord}
                              />
                            }
                          />
                          <FormHelperText>
                            {touched.password ? errors.password : ""}
                          </FormHelperText>
                        </FormControl>
                        <FormControl
                          fullWidth
                          className={classes.spaceTop}
                          error={
                            !!(
                              errors.passwordConfirm && touched.passwordConfirm
                            )
                          }
                        >
                          <InputLabel htmlFor="passwordConfirm">
                            Xác nhận mật khẩu
                          </InputLabel>
                          <Input
                            type="password"
                            name="passwordConfirm"
                            id="passwordConfirm"
                            value={values.passwordConfirm}
                            onChange={handleChange}
                            onBlur={handleBlur}
                          />
                          <FormHelperText>
                            {touched.passwordConfirm
                              ? errors.passwordConfirm
                              : ""}
                          </FormHelperText>
                        </FormControl>
                        <FormControl
                          className={classes.spaceTop}
                          error={!!(errors.agreeTerms && touched.agreeTerms)}
                        >
                          <FormControlLabel
                            control={
                              <Checkbox
                                id="agree-term"
                                name="agreeTerms"
                                color="primary"
                                checked={values.agreeTerms}
                                onChange={handleChange}
                              />
                            }
                            label={
                              <div>
                                <span>Tôi đồng ý với &nbsp;</span>
                                <Link
                                  style={{
                                    textDecoration: "none",
                                    color: "#1e88e5"
                                  }}
                                  to="/"
                                >
                                  điều khoản sử dụng
                                </Link>
                                <span>&nbsp; của chúng tôi</span>
                              </div>
                            }
                          />
                          <FormHelperText>
                            {touched.agreeTerms ? errors.agreeTerms : ""}
                          </FormHelperText>
                        </FormControl>
                        <Button
                          className={classes.colorBtn}
                          variant="contained"
                          color="primary"
                          type="submit"
                          disabled={isSubmitting}
                          fullWidth
                        >
                          {isSubmitting ? (
                            <CircularProgress className={classes.spinner} />
                          ) : (
                            "Đăng ký"
                          )}
                        </Button>
                      </form>
                    );
                  }}
                </Formik>
                {/*<h5 className = {borderC['text-line-center']}>đăng nhập với</h5>*/}
                <Divider style={{ margin: "16px 0" }} />
                <Typography className={AlignS.textCenter}>
                  Đã có tài khoản?
                  <b
                    className={classes.color}
                    onClick={() => handleLoginButton(true)}
                  >
                    {" "}
                    Đăng nhập
                  </b>
                </Typography>
              </Grid>
            </Grid>
          </Paper>
        </Zoom>
      </Modal>
      <Snackbar
        anchorOrigin={{
          vertical: "top",
          horizontal: "right"
        }}
        open={openSnack}
        onClose={() => setOpenSnack(false)}
        autoHideDuration={6000}
        ContentProps={{
          "aria-describedby": "message-register"
        }}
      >
        <SnackbarContent
          className={classes.successSnack}
          aria-describedby="client-snackbar"
          message={
            <span id="client-snackbar" className={classes.message}>
              <CheckCircleIcon />
              &nbsp; Đăng ký thành công
            </span>
          }
        />
      </Snackbar>
    </Fragment>
  );
};

const mapStateToProps = (state: ReducersList) => {
  return {
    animation: state.v_animate
  };
};

const mapDispatchToProps = (dispatch: Dispatch<AnimationAction>) => {
  return {
    handleSignUpAnimation: (status: boolean) =>
      dispatch({
        type: animation.SIGN_UP_BUTTON_CLICK,
        status: status
      }),
    handleLoginButton: (status: boolean) =>
      dispatch({
        type: animation.LOGIN_BUTTON_CLICK,
        status: status
      })
  };
};

export default compose<IProps, any>(
  connect(
    mapStateToProps,
    mapDispatchToProps
  ),
  withStyles(styles)
)(RegisterForm);
