import { ThemeCustom } from '@/components/Theme/Theme';
import createStyles from '@material-ui/core/styles/createStyles';
import withStyles, { WithStyles } from '@material-ui/core/styles/withStyles';
import React, { ComponentType, Fragment } from 'react';
import { compose } from 'recompose';
import Grid from '@material-ui/core/Grid/Grid';
import Typography from '@material-ui/core/Typography/Typography';
import StarRatings from "react-star-ratings";
import classNames from 'classnames';
import mapMarker from '@/assets/SvgIcon/map-marker.svg';
import { RoomIndexRes } from '@/types/Requests/Rooms/RoomResponses';
import Divider from '@material-ui/core/Divider/Divider';
import { Link } from 'react-router-dom';

interface IProps {
  classes?: any
  room: RoomIndexRes
}

const styles: any = (theme: ThemeCustom) => createStyles({
  mapMarker: {
    width: '0.8rem',
  },
  verticalMid: {
    verticalAlign: 'middle',
  },
  imgSize: {
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    objectFit: 'cover',
    width: '100%',
    height: 120,
    verticalAlign: 'middle',
  },
  address: {
    fontSize: '0.8rem',
    fontWeight: 500,
  },
});

// @ts-ignore
const InfoHeader: ComponentType<IProps> = (props: IProps) => {
  const { classes, room } = props;
  // console.log(room);
  return (
    <Fragment>
      {/* <Grid item xs = {12}>
        <Typography variant = 'subtitle2'>THÔNG TIN ĐƠN ĐẶT PHÒNG</Typography>
        <Divider />
      </Grid> */}
      <Grid item xs={12}>
        <img src={`https://s3-ap-southeast-1.amazonaws.com/westay-img/lg/${room!.media.data[0].image}`}
          className={classes.imgSize} alt={`Westay - Homestay cho người việt`} />
      </Grid>
      <Grid item xs={12}>
        <Link style={{ textDecoration: 'none' }} to={`/room/${room!.id}`}><Typography variant='subtitle2'>{room!.details.data[0].name}</Typography></Link>
        <StarRatings
          numberOfStars={room!.standard_point}
          starDimension={`15px`}
          starSpacing={`1px`}
          starEmptyColor={'#ffb40b'}
        /><br />
        <img src={mapMarker} className={classNames(
          classes.mapMarker, classes.verticalMid,
        )} />&nbsp;
        {
          room ?
            <span className={classes.address}>
              {room!.district.data.name},
            {room!.city.data.name}
            </span> : null
        }
      </Grid>
    </Fragment>
  );
};

export default compose<IProps, any>(
  withStyles(styles),
)(InfoHeader);
