import React, {Fragment, useState, useRef, ComponentType, ChangeEvent} from 'react';
import {createStyles, withStyles} from '@material-ui/core/styles';
import {compose} from 'recompose';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Divider from '@material-ui/core/Divider';
import GridContainer from '@/layouts/Grid/Container';
import Tooltip from '@material-ui/core/Tooltip/Tooltip';
import Checkbox from '@material-ui/core/Checkbox/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel/FormControlLabel';
import FormGroup from '@material-ui/core/FormGroup/FormGroup';
import InputAdornment from '@material-ui/core/InputAdornment/InputAdornment';
import IconButton from '@material-ui/core/IconButton/IconButton';
import fb from '@/assets/facebook.png';
import gg from '@/assets/google.png';
import twt from '@/assets/twitter.png';
import houseSweet from '@/assets/house.png';
import {ThemeCustom} from '@/components/Theme/Theme';

const styles:any        = (theme:ThemeCustom) => createStyles ({
  boxSweetHome: {
    [theme!.breakpoints!.down!('xs')]: {
      paddingBottom: 80,
    },
  },
  inputDisabled: {
    padding: '0 3px',
    textAlign: 'center',
    maxWidth: 300,
    width: 230,
  },
  firstLine: {
    margin: '40px 0',
  },
  lightTooltip: {
    background: '#EEEEEE',
    color: theme.palette!.text!.primary,
    boxShadow: theme.shadows![1],
    fontSize: 11,
  },
  arrowPopper: {
    '&[x-placement*="bottom"] $arrowArrow': {
      top: 0,
      left: 0,
      marginTop: '-0.9em',
      width: '3em',
      height: '1em',
      '&::before': {
        borderWidth: '0 1em 1em 1em',
        borderColor: `transparent transparent #EEEEEE transparent`,
      },
    },
    '&[x-placement*="top"] $arrowArrow': {
      bottom: 0,
      left: 0,
      marginBottom: '-0.9em',
      width: '3em',
      height: '1em',
      '&::before': {
        borderWidth: '1em 1em 0 1em',
        borderColor: `#EEEEEE transparent transparent transparent`,
      },
    },
    '&[x-placement*="right"] $arrowArrow': {
      left: 0,
      marginLeft: '-0.9em',
      height: '3em',
      width: '1em',
      top: '30%',
      '&::before': {
        borderWidth: '1em 1em 1em 0',
        borderColor: `transparent #EEEEEE transparent transparent`,
      },
    },
    '&[x-placement*="left"] $arrowArrow': {
      right: 0,
      marginRight: '-0.9em',
      height: '3em',
      width: '1em',
      '&::before': {
        borderWidth: '1em 0 1em 1em',
        borderColor: `transparent transparent transparent #EEEEEE`,
      },
    },
  },
  arrowArrow: {
    position: 'absolute',
    fontSize: 7,
    width: '3em',
    height: '3em',
    '&::before': {
      content: '""',
      margin: 'auto',
      display: 'block',
      width: 0,
      height: 0,
      borderStyle: 'solid',
    },
  },
  root: {
    width: '100%',
    maxWidth: 'max-content',
  },
  listItem: {
    padding: 0,
  },
  ckbView:{
    padding:'0 12px',
  },
  formControlLabel:{
    width:'45%',
    height:'max-content',
  },
  inputAdornment: {
    padding: 5,
  },
  iconSocial:{
    width:24,
    height:24,
  },
  textFieldSocial:{
    width:'80%',
    padding:'4px 0',
  }
